using System;using System.IO;using System.Threading;using System.Threading.Tasks;using ConsoleClient.StartupConfiguration;using ConsoleClient.Users;using Microsoft.Extensions.Configuration;using Microsoft.Extensions.DependencyInjection;using Microsoft.Extensions.Hosting;using Microsoft.Extensions.Logging;using SevenWestMedia.App.DI;namespace ConsoleClient{    public class Program    {        public static async Task Main(string[] args)        {            var startup = new ConsoleStartup();            using (var host = startup.GetHostBuilder().Build())            {                await host.StartAsync();                Run(host);            }        }        private static void Run(IHost host)        {            IUserClient userClient = host.Services.GetService<IUserClient>();            Console.WriteLine("\nPress any key to start application:");            Console.WriteLine("**************************************");            Console.ReadKey();                        Console.Write($"Get user full name by id(53): {userClient.GetUserFullNameById(53)}");            Console.WriteLine("\n----------------------------------------------------\n");                        Console.Write($"Get user first names by age(30): {userClient.GetAllUserFirstNamesByAge(66)}");            Console.WriteLine("\n----------------------------------------------------\n");                        Console.Write($"Get grouped user data by age: \n");                        var userData = userClient.GetUserDataGroupedByAgeAndGender();            Console.WriteLine(string.Join("\n", userData));            Thread.Sleep(3000);                        Console.WriteLine("Application finished.");        }    }}